css:
.kot-header {
    text-align: center;
    border-bottom: 2px dashed #000;
    padding-bottom: 10px;
    margin-bottom: 10px;
}

.kot-title {
    font-size: 24px;
    font-weight: bold;
}

.kot-info {
    font-size: 14px;
    margin: 5px 0;
}

.kot-table {
    font-size: 20px;
    font-weight: bold;
    background: #000;
    color: #fff;
    padding: 5px 10px;
    display: inline-block;
    margin: 10px 0;
}

.kot-items {
    margin: 15px 0;
}

.kot-item {
    border-bottom: 1px dashed #ccc;
    padding: 10px 0;
}

.item-qty {
    font-size: 24px;
    font-weight: bold;
    display: inline-block;
    width: 40px;
}

.item-name {
    font-size: 18px;
    font-weight: bold;
}

.item-name-ar {
    font-size: 16px;
    direction: rtl;
    text-align: right;
}

.item-modifiers {
    font-size: 14px;
    color: #666;
    margin-left: 50px;
}

.item-notes {
    font-size: 14px;
    color: #c00;
    margin-left: 50px;
    font-style: italic;
}

.kot-footer {
    border-top: 2px dashed #000;
    padding-top: 10px;
    margin-top: 10px;
    text-align: center;
}

.kot-time {
    font-size: 12px;
}

.rush-order {
    background: #ff0;
    padding: 5px;
    font-weight: bold;
    text-align: center;
    margin: 10px 0;
}

@media print {
    body {
        width: 80mm;
        margin: 0;
        padding: 5mm;
    }
}
