css:
.receipt {
    font-family: 'Courier New', monospace;
    width: 80mm;
    margin: 0 auto;
}

.receipt-header {
    text-align: center;
    border-bottom: 2px solid #000;
    padding-bottom: 10px;
    margin-bottom: 15px;
}

.restaurant-name {
    font-size: 24px;
    font-weight: bold;
}

.restaurant-name-ar {
    font-size: 20px;
    direction: rtl;
}

.receipt-info {
    font-size: 12px;
    margin: 5px 0;
}

.receipt-divider {
    border-top: 1px dashed #000;
    margin: 10px 0;
}

.order-info {
    margin: 10px 0;
}

.order-info table {
    width: 100%;
    font-size: 12px;
}

.items-table {
    width: 100%;
    border-collapse: collapse;
    margin: 15px 0;
}

.items-table th {
    border-bottom: 1px solid #000;
    text-align: left;
    padding: 5px 0;
    font-size: 12px;
}

.items-table td {
    padding: 8px 0;
    font-size: 12px;
    vertical-align: top;
}

.items-table .item-name {
    max-width: 150px;
}

.items-table .qty {
    text-align: center;
    width: 30px;
}

.items-table .amount {
    text-align: right;
    width: 60px;
}

.item-modifiers {
    font-size: 10px;
    color: #666;
    margin-left: 10px;
}

.totals-section {
    margin-top: 15px;
}

.totals-table {
    width: 100%;
    font-size: 12px;
}

.totals-table td {
    padding: 3px 0;
}

.totals-table .label {
    text-align: left;
}

.totals-table .value {
    text-align: right;
}

.grand-total {
    font-size: 16px;
    font-weight: bold;
    border-top: 2px solid #000;
    border-bottom: 2px solid #000;
    padding: 10px 0;
}

.receipt-footer {
    text-align: center;
    margin-top: 20px;
    padding-top: 10px;
    border-top: 1px dashed #000;
}

.thank-you {
    font-size: 14px;
    font-weight: bold;
}

.thank-you-ar {
    font-size: 14px;
    direction: rtl;
}

.qr-code {
    margin: 15px auto;
    text-align: center;
}

.vat-info {
    font-size: 10px;
    margin-top: 10px;
}

@media print {
    body {
        width: 80mm;
        margin: 0;
        padding: 2mm;
    }
}
